import {useState} from 'react';
import windowSize from 'react-window-size';

import DEMO from "../../../../../../store/constant";

const NavSearch = ({windowWidth }) => {
    const [searchWidth, setSearchWidth] = useState((windowWidth < 992) ? 90 : 0);
    const [searchString, setSearchString] = useState((windowWidth < 992) ? '90px' : '');
    const [isOpen, setIsOpen] = useState((windowWidth < 992));

    searchOnHandler = () => {
        setIsOpen(true);
        const searchInterval = setInterval(() => {
            if (searchWidth >= 91) {
                clearInterval(searchInterval);
                return false;
            }
            setSearchWidth(prevSate => {
                return {
                    searchWidth: prevSate.searchWidth + 15
                }
            });
            setSearchString(prevSate => {
                return {
                    searchString: prevSate.searchWidth + 'px'
                }
            });
        }, 35);
    };

    searchOffHandler = () => {
        const searchInterval = setInterval(() => {
            if (this.state.searchWidth < 0) {
                this.setState({isOpen: false});
                clearInterval(searchInterval);
                return false;
            }
            this.setState(prevSate => {
                return {
                    searchWidth: prevSate.searchWidth - 15,
                    searchString: prevSate.searchWidth + 'px'
                }
            });
        }, 35);
    };

    let searchClass = ['main-search'];
    if (isOpen) {
        searchClass = [...searchClass, 'open'];
    }

    return (
        <Aux>
            <div id="main-search" className={searchClass.join(' ')}>
                <div className="input-group">
                    <input type="text" id="m-search" className="form-control" placeholder="Search . . ." style={{width: this.state.searchString}}/>
                    <a href="#" className="input-group-append search-close" onClick={searchOffHandler}>
                        <i className="feather icon-x input-group-text"/>
                    </a>
                    <span className="input-group-append search-btn btn btn-primary" onClick={this.searchOnHandler}>
                    <i className="feather icon-search input-group-text"/>
                </span>
                </div>
            </div>
        </Aux>
    );
}

export default windowSize(NavSearch);
